mapper.update:
update voice
set voice_re_seq = voice_re_seq + 1
where voice_re_ref = #{voice_re_ref}
  and voice_re_seq > #{voice_re_seq}

update voice
set content  = #{content},
    title    = #{title},
    fileName = #{fileName}
where num = #{num}

mapper.select:

select *
from (select ROWNUM rn, tt.* from (select * from voice order by voice_re_ref desc, voice_re_seq asc) tt)
where rn >= #{startNum}
  and rn <= #{endNum}
